import React, { useMemo, useEffect, useState } from "react";
import { useDropzone } from "react-dropzone";
const baseStyle = {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    justifycontent: "center",
    alignItems: "center",
    padding: "20px",
    borderWidth: 2,
    borderRadius: 2,
    borderColor: "#eeeeee",
    borderStyle: "dashed",
    backgroundColor: "#fafafa",
    color: "#bdbdbd",
    outline: "none",
    transition: "border .24s ease-in-out"
};

const activeStyle = {
    borderColor: "#2196f3"
};

const acceptStyle = {
    borderColor: "#00e676"
};

const rejectStyle = {
    borderColor: "#ff1744"
};



function Csv(props) {
    const [file, setFile] = useState('');
    const [filename, setFilename] = useState('Choose File');
    const {
        getRootProps,
        getInputProps,
        isDragActive,
        isDragAccept,
        isDragReject,
        acceptedFiles,
        open
    } = useDropzone({
        accept: ".csv, text/csv, application/vnd.ms-excel, application/csv, text/x-csv, application/x-csv, text/comma-separated-values, text/x-comma-separated-values",
        noClick: true,
        noKeyboard: true,
        onDrop: acceptedFiles => {
            setFiles(
                acceptedFiles.map(file =>
                    Object.assign(file, {
                        preview: URL.createObjectURL(file)
                    })
                )
            );
        }
    });

    const style = useMemo(
        () => ({
            ...baseStyle,
            ...(isDragActive ? activeStyle : {}),
            ...(isDragAccept ? acceptStyle : {}),
            ...(isDragReject ? rejectStyle : {})
        }),
        [isDragActive, isDragReject]
    );


    useEffect(
        () => () => {
            // Make sure to revoke the data uris to avoid memory leaks
            files.forEach(file => URL.revokeObjectURL(file.preview));
        },
        [files]
    );
    console.log(acceptedFiles[0]);

    const filepath = acceptedFiles.map(file => (

        <li key={file.path}>
            {file.path} - {file.size} bytes
        </li>
    ));

    const postData = async (e) => {
        e.preventDefault();

        const res = await fetch('http://localhost:5000/upload', {
            method: "POST",
            headers: {
                'Content-Type': 'text/csv',
                'Content-disposition': 'attachment; filename=employee.csv'
            },
            body: {
                acceptedFiles
            }

        })
        const data = await res.json()
        if (res.status === 400 || !data) {
            window.alert(data.message)
            console.log(data.message)
        }
        else {
            window.alert("Registration Successful");
            console.log("Registration successful")
        }

    }
    const onChangeHandle = e => {

        setFile(e.target.files[0]);
        setFilename(e.target.files[0].name);

    }
    return (
        <div className="container">
            <div {...getRootProps({ style })}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop some files here</p>
                <em>(Only CSV File will be accepted)</em>
                <div className="mt-3">
                    {/* <button type="button" className="btn btn-primary" onClick={open}>
                        Open File Dialog
                    </button> */}
                    <input type="file" name="file" onChange={onChangeHandle} />
                </div>

            </div>
            <aside>
                <h4>Files</h4>
                <ul>{filepath}</ul>
            </aside>
            <div className="mt-3">
                <button type="button" className="btn btn-primary" onClick={postData}>
                    Add
                </button>
            </div>
        </div>
    );
}

<Csv />

export default Csv
