import React, { useMemo, useEffect, useState } from "react";
import { useDropzone } from "react-dropzone";
import axios from 'axios';
const baseStyle = {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    justifycontent: "center",
    alignItems: "center",
    padding: "20px",
    borderWidth: 2,
    borderRadius: 2,
    borderColor: "#eeeeee",
    borderStyle: "dashed",
    backgroundColor: "#fafafa",
    color: "#bdbdbd",
    outline: "none",
    transition: "border .24s ease-in-out"
};

const activeStyle = {
    borderColor: "#2196f3"
};

const acceptStyle = {
    borderColor: "#00e676"
};

const rejectStyle = {
    borderColor: "#ff1744"
};



function Csv(props) {
    const [files, setFiles] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null)
    const {
        getRootProps,
        getInputProps,
        isDragActive,
        isDragAccept,
        isDragReject,
        acceptedFiles,
        open
    } = useDropzone({
        accept: ".csv, text/csv, application/vnd.ms-excel, application/csv, text/x-csv, application/x-csv, text/comma-separated-values, text/x-comma-separated-values",
        noClick: true,
        noKeyboard: true,
        onDrop: acceptedFiles => {
            setFiles(
                acceptedFiles.map(file =>
                    Object.assign(file, {
                        preview: URL.createObjectURL(file)
                    })
                )
            );
        }
    });

    const style = useMemo(
        () => ({
            ...baseStyle,
            ...(isDragActive ? activeStyle : {}),
            ...(isDragAccept ? acceptStyle : {}),
            ...(isDragReject ? rejectStyle : {})
        }),
        [isDragActive, isDragReject]
    );


    useEffect(
        () => () => {
            // Make sure to revoke the data uris to avoid memory leaks
            files.forEach(file => URL.revokeObjectURL(file.preview));
        },
        []
    );

    console.log(acceptedFiles[0])
    const filepath = acceptedFiles.map(file => (

        <li key={file.path}>
            {file.path} - {file.size} bytes
        </li>
    ));

    const postData = async (e) => {

        setSelectedFile(acceptedFiles[0])
        console.log(selectedFile)

        const formData = new FormData();
        formData.append('file', selectedFile);

        const res = await axios({
            method: 'post',
            url: 'http://localhost:5000/upload',
            data: formData,
            headers: { 'Content-Type': 'multipart/form-data' },
        });
        console.log(res);

    }
    const onChangeHandle = (e) => {
        setSelectedFile(e.target.files[0])
        console.log(selectedFile)
    }
    return (
        <div className="container">
            <div {...getRootProps({ style })}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop some files here</p>
                <em>(Only CSV File will be accepted)</em>
                <div className="mt-3">
                    <button type="button" className="btn btn-primary" name="file" onClick={open}>
                        Open File Dialog
                    </button>
                    <input type="file" name="file" onChange={onChangeHandle} />
                </div>

            </div>
            <aside>
                <h4>Files</h4>
                <ul>{filepath}</ul>
            </aside>
            <div className="mt-3">
                <button type="button" className="btn btn-primary" onClick={postData}>
                    Add
                </button>
            </div>
        </div>
    );
}

<Csv />

export default Csv
