import React, { useEffect, useState } from 'react'
import _ from "lodash"
const Contact = () => {
    const pageSize = 5;

    const [data, setData] = useState([])
    const [pagePost, setpagePost] = useState([])
    const [currPage, setcurrPage] = useState(1)
    const [mail, setMail] = useState({
        subject: "", text: "", email: ""
    })
    let name, value
    const eventHandle = (e) => {

        name = e.target.name
        value = e.target.value

        setMail({ ...mail, [name]: value })

    }
    const getData = async (e) => {
        const res = await fetch('http://localhost:5000/show', {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        })
        const temp = await res.json();
        setData(temp);
        setpagePost(_(temp).slice(0).take(pageSize).value())
        if (res.status === 400 || !data) {
            window.alert(temp.message)
            console.log(temp.message)
        }
        else {
            console.log(data)
        }

    }
    useEffect(() => {
        getData()
    }, []);
    console.log(pagePost)
    const count = Math.ceil(data.length / pageSize)
    if (count === 1) return null;
    const pageNo = _.range(1, count + 1)

    const pagination = (page) => {
        console.log(`${currPage} I am from Pagination`)
        setcurrPage(page);
        const startIndex = (page - 1) * pageSize
        const currPost = _(data).slice(startIndex).take(pageSize).value()
        setpagePost(currPost)
    }
    const previous = () => {

        setcurrPage(currPage - 1)
        console.log(`${currPage} I am from Previous`)

        if (currPage < count && currPage > 0) {

            console.log(`${currPage} I am from Previous inside`)
            const startIndex = (currPage - 2) * pageSize
            const currPost = _(data).slice(startIndex).take(pageSize).value()
            setpagePost(currPost)
        }
        else {
            return pagePost
        }
    }
    const next = () => {
        console.log(`${currPage} I am from next`)
        if (currPage < count) {
            const temp = currPage + 1
            setcurrPage(temp)
            const startIndex = (currPage) * pageSize
            const currPost = _(data).slice(startIndex).take(pageSize).value()
            setpagePost(currPost)
            console.log(`${currPage} I am from next inside`)
        }
        else {

            return pagePost
        }

    }
    const getMail = (mail) => {
        setMail({ ...mail, email: mail })
        console.log(mail)
    }
    const sendMail = async (e) => {
        e.preventDefault();

        const { subject, text, email } = mail


        const res = await fetch('http://localhost:5000/sendmail', {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                email, subject, text
            })
        })
        const temp = await res.json();
        setData(temp);
        setpagePost(_(temp).slice(0).take(pageSize).value())
        if (res.status === 400 || !data) {
            window.alert(temp.message)
            console.log(temp.message)
        }
        else {
            console.log(data)
        }
    }
    return (
        <div>
            <table className="table table-striped mydatatable">
                <thead>
                    <tr>
                        <th scope="col"> </th>
                        <th scope="col">id</th>
                        <th scope="col">First Name</th>
                        <th scope="col">Last Name</th>
                        <th scope="col">Email</th>
                    </tr>
                </thead>
                <tbody>

                    {
                        pagePost.map((val, ind) => {
                            return (
                                <>
                                    <tr key={ind}>
                                        <td><input type="checkbox" onClick={() => getMail(val.email)} /></td>
                                        <td scope="row">{val.id}</td>
                                        <td>{val.firstname}</td>
                                        <td>{val.lastname}</td>
                                        <td>{val.email}</td>
                                    </tr>
                                </>

                            )
                        })
                    }
                </tbody>
            </table>
            <nav className="d-flex justify-content-center align-items-center">
                <button type="button" className="btn btn-primary mb-2" style={{ fontSize: "13px" }} onClick={previous}>
                    previous
                </button>
                <ul className="pagination mt-4">
                    {
                        pageNo.map((val, ind) => {
                            return (
                                <li className={val === currPage ? "page-item active" : "page-item"} key={ind}>
                                    <p className="page-link" onClick={() => pagination(val)}>
                                        {val}
                                    </p>
                                </li>
                            )

                        })
                    }

                </ul>
                <button type="button" className="btn btn-primary mb-2" style={{ fontSize: "13px" }} onClick={next} >
                    next
                </button>
            </nav>

            <input type="text" name="subject" value={mail.subject} onChange={eventHandle} />
            <input type="text" name="text" value={mail.body} onChange={eventHandle} />
            <button type="button" className="btn btn-primary mb-2" onClick={sendMail} >
                send Mail
            </button>
        </div>
    )

}

export default Contact
