import React, { useEffect, useState } from 'react'
import _ from "lodash"
const Show = () => {
    const pageSize = 5;

    const [data, setData] = useState([])
    const getData = async (e) => {

        const res = await fetch('http://localhost:5000/show', {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        })
        const temp = await res.json();
        setData(temp);
        if (res.status === 400 || !data) {
            window.alert(temp.message)
            console.log(temp.message)
        }
        else {
            console.log(data)
        }

    }
    useEffect(() => {
        getData()
    }, []);
    const count = Math.ceil(data.length / pageSize)
    if (count === 1) return null;
    const pageNo = _.range(1, count + 1)
    return (
        <div>
            <table className="table table-striped mydatatable">
                <thead>
                    <tr>
                        <th scope="col">id</th>
                        <th scope="col">First Name</th>
                        <th scope="col">Last Name</th>
                        <th scope="col">Email</th>
                    </tr>
                </thead>
                <tbody>

                    {
                        data.map((val, ind) => {
                            return (
                                <>
                                    <tr>
                                        <th scope="row">{val.id}</th>
                                        <td>{val.firstname}</td>
                                        <td>{val.lastname}</td>
                                        <td>{val.email}</td>
                                    </tr>
                                </>

                            )
                        })
                    }


                </tbody>
            </table>
            <nav className="d-flex justify-content-center align-items-center">
                <ul className="pagination">
                    {
                        pageNo.map((val, ind) => {
                            return (
                                <li className="page-link" key={ind}>{val}</li>
                            )

                        })
                    }

                </ul>
            </nav>
        </div>
    )

}

export default Show
