import React, { useState } from 'react'
const Add = () => {
    const [user, setUser] = useState({
        name: "", email: "", password: "", cpassword: ""
    })
    let name, value, data;
    const eventHandle = (e) => {
        name = e.target.name
        value = e.target.value

        setUser({ ...user, [name]: value })
    }

    // const postData = async (e) => {
    //     e.preventDefault();
    //     const { name, email, password, cpassword } = user
    //     const res = await fetch('http://localhost:5000/signup', {
    //         method: "POST",
    //         headers: {
    //             "Content-Type": "application/json"
    //         },
    //         body: JSON.stringify({
    //             name, email, password, cpassword
    //         })
    //     })
    //     data = await res.json()
    //     if (res.status === 400 || !data) {

    //         window.alert(data.message)
    //         console.log(data.message)

    //     }
    //     else {
    //         window.alert("Registration Successful");
    //         console.log("Registration successful")
    //         history.push('./login')
    //     }

    // }
    return (
        <div className="signup-form">
            <div className="signup-content">
                <div className="row">
                    <div className="col-md-6">
                        <h1 className="text-center">Add Employee</h1>
                        <form method="POST" >
                            <div className="form-group mt-5">
                                <div className="input-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text">
                                            <span className="fa fa-user"></span>
                                        </span>
                                    </div>
                                    <input type="text" className="form-control" name="firstname"
                                        value={user.name} onChange={eventHandle} placeholder="First Name" required="required" />
                                </div>
                                <div className="input-group">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text">
                                            <span className="fa fa-user"></span>
                                        </span>
                                    </div>
                                    <input type="text" className="form-control" name="lastname"
                                        value={user.name} onChange={eventHandle} placeholder="last Name" required="required" />
                                </div>

                                <div className="input-group mt-3">
                                    <div className="input-group-prepend">
                                        <span className="input-group-text">
                                            <span className="fa fa-paper-plane"></span>
                                        </span>
                                    </div>
                                    <input type="text" className="form-control" name="email"
                                        value={user.email} onChange={eventHandle} placeholder="Email Address" required="required" />
                                </div>


                            </div>
                        </form>

                    </div>
                </div>
            </div>

        </div>

    )
}

export default Add
