import React, { useEffect, useState } from 'react'

const Show = () => {
    const [data, setData] = useState([])
    const getData = async (e) => {
        const res = await fetch('http://localhost:5000/show', {
            method: "GET",
            headers: {
                "Content-Type": "application/json"
            },
        })
        setData(data.concat(await res.json))
        if (res.status === 400 || !data) {
            window.alert(data.message)
            console.log(data.message)
        }
        else {
            console.log(data[0])
        }

    }
    useEffect(() => {
        getData()
    }, []);
    return (
        <div>
            <table class="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">id</th>
                        <th scope="col">First Name</th>
                        <th scope="col">Last Name</th>
                        <th scope="col">Email</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td>Larry</td>
                        <td>the Bird</td>
                        <td>@twitter</td>
                    </tr>
                </tbody>
            </table>
        </div>
    )
}

export default Show
